<template>
  <main>
    <div class="flex min-h-full flex-col justify-center px-6 py-12 lg:px-8">
      <div class="sm:mx-auto sm:w-full sm:max-w-sm">
        <img
          class="mx-auto h-20 w-auto"
          src="data:image/png;base64,"
          alt="Your Company"
        />
        <h2
          class="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900"
        >
          Vous connecter Ã  votre compte
        </h2>
      </div>

      <div class="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
        <form class="space-y-6">
          <div>
            <label
              for="email"
              class="block text-sm font-medium leading-6 text-gray-900"
              >Email
            </label>
            <div class="mt-2">
              <input
                v-model="email"
                id="email"
                name="email"
                type="email"
                autocomplete="email"
                required
                class="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>
          <!--password-->
          <div>
            <div class="flex items-center justify-between">
              <label
                for="password"
                class="block text-sm font-medium leading-6 text-gray-900"
                >Mot de passe</label
              >
            </div>
            <div class="mt-2">
              <input
                v-model="password"
                id="password"
                name="password"
                type="password"
                autocomplete="current-password"
                required
                class="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>

          <div>
            <button
              type="button"
              @click="login"
              class="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
              Se connecter
            </button>
          </div>
        </form>
        <!--error-->
        <p
          v-if="alert"
          class="rounded text-center bg-red-500 text-white p-2 my-2"
        >
          {{ alertMsg }}
        </p>
        <p class="mt-10 text-center text-sm text-gray-500">
          Pas encore membre?
          <router-link class="text-blue-500 hover:text-blue-700" to="/register"
            >s'inscrire</router-link
          >
        </p>
      </div>
    </div>
  </main>
</template>
<style scoped>
input {
  padding-left: 10px;
}
</style>
<script setup>
import { useRouter } from "vue-router";
import { useStore } from "vuex";
import { ref } from "vue";
const store = useStore();
const router = useRouter();
const alertMsg = ref("");
const alert = ref(false);

//user info

const email = ref("");
const password = ref("");

const login = async () => {
  const user = {
    email: email.value,
    password: password.value,
  };
  const result = await store.dispatch("login", user);
  if (result.user == null) {
    lanceAlert(result.message);
  } else {
    router.push("/home");
  }
};
const lanceAlert = (message) => {
  console.log(message);
  alert.value = true;
  alertMsg.value = message;
  setTimeout(() => {
    alert.value = false;
  }, 3000);
};
</script>
